import React from "react";
import "./css/Contact.css";
import Header from "./Header";
import Footer from "./Footer";
import axios from "axios";

var nodemailer = require("nodemailer")

var transport = nodemailer.createTransport({
  host: "smtp.qq.com",
  secureConnection: true,
  port: 465,
  auth: {
    user: "943616137@qq.com",
    pass: "wevdtwlfvnezbahi"
  }
})

class Contact extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
      message: "Leave your message here",
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value,
    });
  }

  handleSubmit(event) {
    event.preventDefault();

    // Contact info that stores in database
    const payload = 
    {
      firstname: this.state.firstName,
      lastname: this.state.lastName,
      email: this.state.email,
      phoneNum: this.state.phone,
      message: (this.state.message === "Leave your message here") ? " " : this.state.message
    }

    const mailOptions = {
      from: "Ava's-Angels 943616137@qq.com",
      to: "scyws1@nottingham.ac.uk",
      subject: "Hello",
      text: "Hi",
      html: "<b>haha</b>"
    }

    // Post to api
    axios(
      {
        url: 'http://localhost:4000/contact',
        method: 'POST',
        data: payload,
      }
    )
    .then(() => 
    {
      console.log('Data has been sent to the server!')
      
      //Send the confirmation email to user
      transport.sendMail(mailOptions, function(err, response) {
        if (err) console.log(err)
        else console.log(response)
      })
      window.location.href = '/Intermediate';
    })
    .catch(() => 
    {
      console.log('Fail to send to the server!')
    }
    )
  }

  render() {
    return (
      <div>
        <Header />

        <div className="contact_header"> Contact </div>

        <div align="center">
          <form className="contact_form" onSubmit={this.handleSubmit}>
            <div>
              <label>
                <strong>First Name *</strong>
              </label>
              <input
                name="firstName"
                type="text"
                value={this.state.firstName}
                onChange={this.handleInputChange}
                required
              />
            </div>
            <div>
              <label>
                <strong>Last Name *</strong>
              </label>
              <input
                name="lastName"
                type="text"
                value={this.state.lastName}
                onChange={this.handleInputChange}
                required
              />
            </div>
            <div>
              <label>
                <strong>Email Address</strong>
              </label>
              <input
                name="email"
                type="text"
                value={this.state.email}
                onChange={this.handleInputChange}
                required
              />
            </div>
            <div>
              <label>
                <strong>Phone number *</strong>
              </label>
              <input
                name="phone"
                type="text"
                value={this.state.phone}
                onChange={this.handleInputChange}
                required
              />
            </div>
            <div>
              <label>
                <strong>Message</strong>
              </label>
              <textarea
                name="message"
                value={this.state.message}
                onChange={this.handleInputChange}
              />
            </div>
            <button type="submit">Submit</button>
            <button type="reset">Reset</button>
          </form>
        </div>

        <Footer />
      </div>
    );
  }
}

export default Contact;
