import React from 'react';
import './css/Charity.css';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import Header from './Header';
import Footer from "./Footer";
import SearchIcon from "./resource/charitySearch.jpg";

const tileData = [
    {
        img: "https://birminghammind.org/",
        title: 'birminghammind',
        author: 'author',
    }
]

const useStyles = makeStyles(theme => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      backgroundColor: theme.palette.background.paper,
    },
    gridList: {
      flexWrap: 'nowrap',
      transform: 'translateZ(0)',
    },
    title: {
      color: theme.palette.primary.light,
    },
    titleBar: {
      background:
        'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
    },
  }));

class Search extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            searchURL: ''
        }

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleInputChange (event) {
        this.setState({
            [event.target.name] : "https://www.google.com/search?q=" + event.target.value 
        })
    }

    handleSubmit (event){
        event.preventDefault();
        window.open(this.state.searchURL);
    }

    render(){
        return(
            <div>

                <div class = "charity_header">Charities</div>

                <div class="search-container">
                    <img src={SearchIcon} alt="search" />
                    <form>
                        <input 
                            type="text" 
                            placeholder="Search.."
                            name="searchURL"
                            onChange={this.handleInputChange}
                            required />
                        <button 
                            type="submit" 
                            onClick={this.handleSubmit}>
                            Search
                        </button>
                    </form>
                </div>

                <h2 class = "charityListHeader">
                    Below are a selection of local charities with links to their webpages
                </h2>

                      <div className={classes.root}>
                        <GridList className={classes.gridList} cols={2.5}>
                          {tileData.map(tile => (
                            <GridListTile key={tile.img}>
                              <img src={tile.img} alt={tile.title} />
                              <GridListTileBar
                                title={tile.title}
                                classes={{
                                  root: classes.titleBar,
                                  title: classes.title,
                                }}
                                actionIcon={
                                  <IconButton aria-label={`star ${tile.title}`}>
                                    <StarBorderIcon className={classes.title} />
                                  </IconButton>
                                }
                              />
                            </GridListTile>
                          ))}
                        </GridList>
                      </div>
                    );
                  }


                <div>
                    <p>
                        Birmingham Mind
                    </p>
                    <a href="https://birminghammind.org/" target = "_blank" rel= "noopener noreferrer">
                        <img src="https://upload.wikimedia.org/wikipedia/en/0/01/Mind-logo.gif" />
                    </a>
                </div>

                <div>
                    <p>
                        Breast Cancer Support Charity
                    </p> 
                    <a href="https://breastcancersupport.org.uk/" target = "_blank" rel= "noopener noreferrer">
                        <img src="https://breastcancersupport.org.uk/wp-content/uploads/2015/11/logo.png" />
                    </a>
                </div>

            </div>
            )
        }
    }

function Charity(){

}
export default Charity;
