import React from 'react';
import './App.css';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import {grey} from "@material-ui/core/colors";

const FoodAccomContactFBButton = withStyles({
    root: {
        fontSize: "2.5vw",
        boxShadow:"none",
        textTransform:"none",
        fontWeight:"bold",
        margin:"1vw, 2vw, 1vw, 2vw",
        fontFamily: "Georgia",
        height:"15vw",
        wdith:"30vw",
        backgroundColor: grey[500],
        "&:hover": {
            backgroundColor: grey[700]
        }
    }
})(Button);

const CharityAboutButton = withStyles({
    root: {
        fontSize: "2.5vw",
        boxShadow:"none",
        textTransform:"none",
        fontWeight:"bold",
        margin:"1vw, 2vw, 1vw, 2vw",
        fontFamily: "Georgia",
        height:"15vw",
        wdith:"30vw",
        backgroundColor: grey[500],
        "&:hover": {
            backgroundColor: grey[700]
        }
    }
})(Button);

const useStyles = makeStyles(theme => ({
    margin: {
        margin: theme.spacing("8vw"),
    }
  }));


function App() {
    const classes = useStyles();

    return (
    <div>
        <header>
        <div class = "pageHeader" align = "center">
            <a href = "https://www.avas-angels.com/">
                <img src="https://www.avas-angels.com/images/HiResLogo.png" alt = "Ava's Angel"/>
            </a>
        </div>
        </header>

        <div class = "introduction">
        <div class = "title">
            <a href = "https://www.avas-angels.com/">
                <p>{"Ava's Angels In Action"}</p>
            </a>
        </div>
        <div>
            <p class = "content">
                This is a responsive web application designed to provide information about the local area such as food, charity, accomodation.<br /> 
                In addtion, as well as providing the opportunity for user to leav feedback or contact details for the charity.<br />
                Finally, a virtual assistant will help you with quick queries.<br />
            </p>
        </div>
        </div>
        <br />

        <br />
        <div>
            <div>
                <FoodAccomContactFBButton variant="contained" color="primary" className={classes.margin}>Food</FoodAccomContactFBButton>
                <FoodAccomContactFBButton variant="contained" color="primary" className={classes.margin}>Accommodation</FoodAccomContactFBButton>
            </div>
            <div>
                <CharityAboutButton variant="contained" color="primary">Charities</CharityAboutButton>
                <img src="https://scontent.cdninstagram.com/v/t51.2885-15/sh0.08/e35/s640x640/75244443_161725248223830_1550331828311660529_n.jpg?_nc_ht=scontent.cdninstagram.com&oh=72acfbed5db6d7cc4e5d42f9e56fafb6&oe=5E6FA1A4" class = "button_image"/>
                <CharityAboutButton variant="contained" color="primary">About</CharityAboutButton>
            </div>
            <div>
                <FoodAccomContactFBButton variant="contained" color="primary" className={classes.margin}>Leave Contact Info</FoodAccomContactFBButton>
                <FoodAccomContactFBButton variant="contained" color="primary" className={classes.margin}>Feedback</FoodAccomContactFBButton>
            </div>
        </div>

        <div class="footer">
            <div class="aboutus">
            <p class="bold" id = "ABOUT">About Us</p>
            <p> Helping families with sick children in hospital. Providing relief of sickness and suffering among children being cared for in hospital by providing items, 
                    services and emotional support for families to enable them to visit, spend quality time with and care for such children.</p>
            </div>

            <div class = "space"></div>

            <div class="contact">
            <p class="bold" id = "CONTACT">Contact</p>
            <p>Contact us: team@avas-angles.com<br />
                Phone : 07951531748<br /></p>   
            </div>
        </div>
    </div>
    );
}

export default App;
