    ];
    this.state = { text: this.descriptionArray[this.index] }; //State updates label text
  }

  leftClick = () => {
    //Function for left clicks
    if (this.index > 0) {
      this.index--;
    } else {
      this.index = 3;
    }

    console.log("Left click ðŸ”¥, index is " + this.index);
    this.setState({ text: this.descriptionArray[this.index] });
    document.getElementById("picture").src = this.imageArray[this.index];
  };

  rightClick = () => {
    //Functions for right clicks
    if (this.index < 3) {
      this.index++;
    } else {
      this.index = 0;
    }

    console.log("right click ðŸ”¥, index is " + this.sindex);
    this.setState({ text: this.descriptionArray[this.index] });
    document.getElementById("picture").src = this.imageArray[this.index];
  };

  render() {
    //Component renders from here
    return (
      <div id="root" className="About">
        <header className="App-header">
          <img
            id="logo"
            src="https://www.avas-angels.com/images/HiResLogo.png"
            className="logo"
            alt="Ava's Angels logo"
          />

          <img
            id="picture"
            src={this.imageArray[this.index]}
            className="picture"
            alt="Bunk bar"
          />

          <button onClick={this.rightClick} className="right-button">
            {" "}
            >{" "}
          </button>
          <button onClick={this.leftClick} className="left-button">
            {" "}
            &lt;{" "}
          </button>

          <p> {this.state.text} </p>
        </header>
      </div>
    );
  }
}

